import React, { useState, useEffect } from "react";
import { fetchGenre, fetchMovieByGenre, movieSearch } from "../../service/index";
import Footer from '../Footer'
import "react-bootstrap-carousel/dist/react-bootstrap-carousel.css";
import { Link } from "react-router-dom";

export function Home() {
  const [genres, setGenres] = useState([]);
  const [movieByGenre, setMovieByGenre] = useState([]);
  const [movieTitle, setSearchTitle] = useState([]);

  useEffect(() => {
    const fetchAPI = async () => {
      setGenres(await fetchGenre());
      setMovieByGenre(await fetchMovieByGenre(28));
      setSearchTitle(await movieSearch(title));
    };

    fetchAPI();
  }, []);

  const handleGenreClick = async (genre_id) => {
    setMovieByGenre(await fetchMovieByGenre(genre_id));
  };

  const moviesSearched = movieTitle.map((item, index) => {
    return (
      <div className="col-md-3 col-sm-6" key={index}>
        <div className="card">
          <Link to={`/movie/${item.id}`}>
            <img className="img-fluid" src={item.poster} alt={item.title}></img>
          </Link>
        </div>
        <div className="mt-3">
          <p style={{ fontWeight: "bolder" }}>{item.title}</p>
          <p>Rated: {item.rating}</p>
        </div>
      </div>
    );
  });

  const genreList = genres.map((item, index) => {
    return (
      <li className="list-inline-item" key={index}>
        <button
          type="button"
          style={{color:'gold'}}
          className="btn btn-outline-info"
          onClick={() => {
            handleGenreClick(item.id);
          }}
        >
          {item.name}
        </button>
      </li>
    );
  });

  const movieList = movieByGenre.map((item, index) => {
    return (
      <div className="col-md-3 col-sm-6" key={index}>
        <div className="card">
          <Link to={`/movie/${item.id}`}>
            <img className="img-fluid" src={item.poster} alt={item.title}></img>
          </Link>
        </div>
        <div className="mt-3">
          <p style={{ fontWeight: "bolder" }}>{item.title}</p>
          <p>Rated: {item.rating}</p>
        </div>
      </div>
    );
  });

  let {title} = "pandorum"

  function handleTitle(Title){
    title = Title;
  }

  return (
    <div className="container">
      <div className="row mt-4">
        <div className="col-1">
            <h1 style={{color: 'white'}}>Home</h1>
        </div>
      </div>

      <hr style={{ borderTop: "1px solid #5a606b" }}></hr>

      <div className="row mt-3">
        <div className="col">
          <h3 className="mt-3" style={{color: 'white'}}>Genres:</h3>
          <ul className="list-inline">{genreList}</ul>
        </div>
      </div>

      <h3 className="mt-3" style={{color: 'white'}}>Search</h3>
      <form >
        <p>
          <input type='text' placeholder='Looking for something?' name='title'></input>
          <button style={{color: 'black'}} onClick={handleTitle(title)}>Lets go!</button>
        </p>
      </form>

      <hr style={{ borderTop: "1px solid #5a606b" }}></hr>
      <h3 style={{color: 'white'}}>{"Movies & TV Shows"}</h3>
      <div className="row mt-3">
        <div className="col">
          <div className="float-right">
            <i className="far fa-arrow-alt-circle-right"></i>
          </div>
        </div>
      </div>
      <div className="row mt-3">{movieList}</div>
      <div className="row mt-3">{moviesSearched}</div>

    <hr className="mt-5" style={{ borderTop: "1px solid #5a606b" }}></hr>
    <Footer/>
    </div>
  );
}
